"use strict";

var _common = require("test/common");

describe('Scale - nearest neighbor', function () {
  let original;
  beforeEach(async () => {
    original = await (0, _common.load)('resize/5x5a_5.png');
  });
  const tests = [1, 2, 3, 4, 5, 10, 13, 18];
  tests.forEach(function (test) {
    it(`5x5 -> ${test}x${test}`, async () => {
      const expected = await (0, _common.load)(`resize/5x5a_${test}.png`);
      expect((0, _common.getHash)(original.resize({
        height: test,
        width: test
      }))).toBe((0, _common.getHash)(expected));
    });
  });
});